// SPDX-License-Identifier: MIT
//pragma solidity >0.4.0 <0.7.0
//pragma solidity 0.8.9;
pragma solidity >=0.4.21 <0.8.9;

contract StepCoin {

  // state variables
  address payable public owner;
  uint public awardCount;

modifier isOwner () {
    require(msg.sender == owner);
    _;
  }

modifier verifyCaller (address _address) {
    require (msg.sender == _address);
    _;
  }

modifier paidEnough(uint _price) {
    require(msg.value >= _price);
    _;
  }

modifier forSale(uint _i) {
    require(awards[_i].state == State.New && awards[_i].seller != address(0));
    _;
  }

  // enrollment to the StepCoin
  mapping (address => bool) public enrolled;
  // keep clients balances of stepcoints
  mapping (address => uint) public balances;
  // mapping all awards from shops
  mapping (uint256 => Award) public awards;

  // enum states of award: New, Redeemed, Used
  enum State {
    New,
    Redeemed,
    Used
  }

  // struct Award
  struct Award {
    string name;
    uint count;
    uint price;
    State state;
    address payable seller;
    address payable buyer;
  }

  // Events
  event LogEnrolled(address accountAddress);
  event LogBalance(address accountAddress, uint amount);
  event LogRedeem(address accountAddress, uint redeemAmount, uint newBalance);


  //constructor() public {
    // 1. Set the owner to the transaction sender
   // owner = payable (msg.sender);
  //}

// function to enroll to the system
  function enroll() public returns(bool){
    enrolled[msg.sender] = true;
    emit LogEnrolled(msg.sender);
    return enrolled[msg.sender];
  }

// fuction to deposit steps into coins
// maybe deposit should take steps from user and convert to coint??
  function depositSteps(uint step) public payable returns (uint) {
    require(enrolled[msg.sender]== true, "Please enroll");
    balances[msg.sender] += step;
    emit LogBalance(msg.sender, balances[msg.sender]);
    return balances[msg.sender];
  }

// function to check balance of coins
  function checkBalance() public view returns(uint){
    return balances[msg.sender];
  }

// function to add Award to the site
  function addAward(string memory _name, uint _price) public returns(bool){
    awards[awardCount] = Award({
      name: _name,
      count: awardCount,
      price: _price,
      state: State.New,
      seller: msg.sender,
      buyer: address(0)
    });

    awardCount = awardCount +1;
    return true;
  }

// function to redeem stepcoin and get Award
// maybe need to add some modifier like Award is New and Buyer has enough coins to redeem
// function getAward(uint _i) payable public {
//    awards[_i].buyer = payable(msg.sender);
//    awards[_i].state = State.Redeemed;
//    awards[_i].seller.transfer(awards[_i].price);
//    uint newBalance = balances[msg.sender]-awards[_i].price;
//    emit LogRedeem(awards[_i].buyer, awards[_i].price, newBalance);
//  } 

function buyAward(uint _i) public payable forSale(_i) returns (uint){
   //(bool success, ) = awards[_i].seller.call{ value: _coin }("");
   // require(success, "Transfer failed.");
    require(balances[msg.sender]>= awards[_i].price, "Not enough Stepcoins");
    awards[_i].buyer = msg.sender;
    awards[_i].state = State.Redeemed;
    balances[msg.sender] -= awards[_i].price;
    emit LogRedeem(awards[_i].buyer, awards[_i].price, balances[msg.sender]);
    return balances[msg.sender];
}

// function to use Award
  function useAward(uint _awardCount) payable public{
    awards[_awardCount].state = State.Used;
  }
}
